import { Component } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';

@Component({
  selector: 'app-form',
  templateUrl: './form.component.html',
  styleUrls: ['./form.component.css']
})
export class FormComponent {
userForm!:FormGroup
constructor(private fb:FormBuilder){}
ngOnInit(){
  this.userForm =this.fb.group({
    fName:['',Validators.required],
    LName:['',Validators.required],
    email:['',Validators.required],
    cEmail:['',Validators.required]
  })
  console.log(this.userForm,'5555')
}
formSubmit(): void {
  if (this.userForm.invalid) {
    // Mark all controls as touched to trigger error messages
    this.userForm.markAllAsTouched();
  } else {
    // Handle valid form submission
    console.log('Form Submitted:', this.userForm.value);
  }
}
}
<p>form works!</p>
<div>
  <form [formGroup]="userForm" (ngSubmit)="formSubmit()">
    <label> firstName </label>
    <input type="text" formControlName="fName" />
    <div *ngIf="userForm.get('fName')?.touched && userForm.get('fName')?.invalid">enter the firstName</div>
    <label> lastName </label>
    <input type="text" formControlName="LName" [disabled]="userForm.controls['fName']?.value === 123" />
    <div *ngIf="userForm.get('LName')?.touched && userForm.get('LName')?.invalid">enter the lastName</div>
    <label> Email </label>
    <input type="text" formControlName="email" />
    <div *ngIf="userForm.get('email')?.touched && userForm.get('email')?.invalid">enter the Email</div>
    <label> ConformEmail </label>
    <input type="text" formControlName="cEmail" />
    <div *ngIf="userForm.get('cEmail')?.touched && userForm.get('cEmail')?.invalid">enter the ConformEmail</div>
    <button type="submit">Submit</button>
  </form>
</div>
