    <label> lastName </label>
    <input type="text" formControlName="LName" />
    <div
      *ngIf="userForm.get('LName')?.touched && userForm.get('LName')?.invalid"
    >
      enter the lastName
    </div>
    <label> Email </label>
    <input type="text" formControlName="email" />
    <div
      *ngIf="userForm.get('email')?.touched && userForm.get('email')?.invalid"
    >
      enter the Email
    </div>
    <label> ConformEmail </label>
    <input type="text" formControlName="cEmail" />
    <div
      *ngIf="userForm.get('cEmail')?.touched && userForm.get('cEmail')?.invalid"
    >
      enter the ConformEmail
    </div>
  </form>
  <button *ngIf="!getData?.flag" (click)="formSubmit()">Submit</button>
  <button  *ngIf="getData?.flag" (click)="update()">Update</button>
</div>
